import React from 'react'
import styled from 'styled-components'

const Articles = ({posts}) => {
    return(
        <div>
            Welcome to articles component !!!
            {posts.map((article, key) => (
                <div>
                    <h2>{article.title}</h2>
                    <p>{article.description}</p>
                    <span>{article.author}</span>
                </div>
            ))}
        </div>
    )
}

export default Articles

//Main container
