import React from 'react'
import styled from 'styled-components'

const AddArticle = () => {
    return(
        <form>
            <div className="form-group">
                <label htmlFor="author">Author Name</label>
                <input type="text" className="form-control" placeholder="Enter the author name"></input>
            </div>
            <div className="form-group">
                <label htmlFor="title">Title</label>
                <input type="text" className="form-control" placeholder="Enter the title of the blog"></input>
            </div>
            <div class="form-group">
                <label htmlFor="description">Content</label>
                <textarea class="form-control" rows="3"></textarea>
            </div>
            <button type="submit" className="btn btn-primary">Post Article</button>
        </form>
    )
}

export default AddArticle

//Main container
const AddArticleContainer = styled.div`
    margin: 3rem auto;
    padding: 4rem;
    width: 31.25rem;
`;
