import React from 'react'
import styled from 'styled-components'
import loading_animation from './../loader2.gif'

const Articles = ({posts}) => {
    return(
        <MainContainer>
            {!posts.length ? (<img src={loading_animation} alt="Loading..."></img>):(
                posts.map((article, key) => (
                <div className="container">
                    <br/>
                    <h2>{article.title}</h2>
                    <h4></h4>
                    <p>{article.description}</p>
                    <span className="badge badge-secondary p-2">{article.author}</span>
                    <div className="row my-5">
                        <div className="col-sm-2">
                            <a href="" className="btn btn-outline-success">Edit Article</a>
                        </div>    
                        <div className="col-sm-2">
                            <a href="" className="btn btn-outline-danger">Delete Article</a>
                        </div>                      
                    </div>
                    <hr/>
                </div>)
            ))}
        </MainContainer>
    )
}

export default Articles

//Main container
const MainContainer = styled.div`
    margin: 7rem 0;

    img {
        width: 40rem;
        display: block;
        margin: 0 auto;
    }
    .container{
        background: var(--white)
    }

`;
